CREATE TABLE klient (
    id_klienta SERIAL PRIMARY KEY,
    imie VARCHAR(50) NOT NULL,
    nazwisko VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    telefon VARCHAR(15)
);

CREATE TABLE zlecenie (
    id_zlecenia SERIAL PRIMARY KEY,
    id_klienta INT NOT NULL,
    data_przyjecia DATE NOT NULL,
    status VARCHAR(20) NOT NULL CHECK (status IN ('przyjete', 'w realizacji', 'zakonczone')),
    opis_usterki TEXT,
    FOREIGN KEY (id_klienta) REFERENCES klient(id_klienta)
        ON DELETE CASCADE
);

CREATE TABLE sprzet (
    id_sprzetu SERIAL PRIMARY KEY,
    id_zlecenia INT NOT NULL,
    marka VARCHAR(50),
    model VARCHAR(50),
    numer_seryjny VARCHAR(50),
    FOREIGN KEY (id_zlecenia) REFERENCES zlecenie(id_zlecenia)
        ON DELETE CASCADE
);

CREATE TABLE pracownik (
    id_pracownika SERIAL PRIMARY KEY,
    imie VARCHAR(50) NOT NULL,
    nazwisko VARCHAR(50) NOT NULL,
    specjalizacja VARCHAR(100)
);

CREATE TABLE naprawa (
    id_naprawy SERIAL PRIMARY KEY,
    id_sprzetu INT NOT NULL,
    id_pracownika INT NOT NULL,
    koszt NUMERIC(10,2) CHECK (koszt >= 0),
    data_naprawy DATE NOT NULL,
    FOREIGN KEY (id_sprzetu) REFERENCES sprzet(id_sprzetu)
        ON DELETE CASCADE,
    FOREIGN KEY (id_pracownika) REFERENCES pracownik(id_pracownika)
        ON DELETE SET NULL
);

