CREATE OR REPLACE FUNCTION statystyki_klientow()
RETURNS TABLE (
    id_klienta INT,
    nazwisko VARCHAR,
    liczba_zlecen BIGINT, 
    suma_kosztow NUMERIC
) AS $$
BEGIN
    RETURN QUERY
    SELECT k.id_klienta, k.nazwisko, COUNT(DISTINCT z.id_zlecenia) AS liczba_zlecen,
           COALESCE(SUM(n.koszt), 0) AS suma_kosztow
    FROM klient k
    LEFT JOIN zlecenie z ON k.id_klienta = z.id_klienta
    LEFT JOIN sprzet s ON z.id_zlecenia = s.id_zlecenia
    LEFT JOIN naprawa n ON s.id_sprzetu = n.id_sprzetu
    GROUP BY k.id_klienta, k.nazwisko
    ORDER BY suma_kosztow DESC;
END;
$$ LANGUAGE plpgsql;

