CREATE OR REPLACE FUNCTION waliduj_koszt_naprawy()
RETURNS TRIGGER AS $$
BEGIN
    IF NEW.koszt > 1000 THEN
        RAISE EXCEPTION 'Koszt naprawy nie może przekraczać 1000';
    END IF;
    RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER trg_waliduj_koszt
BEFORE INSERT OR UPDATE ON naprawa
FOR EACH ROW
EXECUTE FUNCTION waliduj_koszt_naprawy();

